import React, { useState } from 'react';
import TodoList from './TodoList'; // Import TodoList component
import './App.css'; // Import custom CSS for styling

function App() {
  const [tasks, setTasks] = useState([]); // State to store tasks
  const [newTask, setNewTask] = useState(''); // State to store the new task

  // Handle adding new task
  const handleAddTask = () => {
    if (newTask.trim() !== '') {
      setTasks([...tasks, newTask]);
      setNewTask('');
    }
  };

  // Handle deleting a task
  const handleDeleteTask = (taskToDelete) => {
    setTasks(tasks.filter((task) => task !== taskToDelete));
  };

  return (
    <div className="App">
      <div className="todo-container">
        <h1>ToDo List</h1>

        {/* Input field for adding a new task */}
        <input
          type="text"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)} // Update the newTask state
          placeholder="Enter new task"
        />
        <button onClick={handleAddTask}>Add Task</button> {/* Add Task Button */}

        {/* Render the TodoList component, passing tasks and delete handler */}
        <TodoList tasks={tasks} onDeleteTask={handleDeleteTask} />
      </div>
    </div>
  );
}

export default App;
